resources.string:
uniform mat4 MVP;

attribute vec4 aPosition;
attribute vec4 aColor;

varying vec4 vColor;

void main() {
    gl_Position = MVP * aPosition;
    vColor = aColor;
}

precision mediump float;

varying vec4 vColor;

void main() {
    gl_FragColor = vColor;
}

attribute vec4 aPosition;
attribute vec2 aTextureCoord;

varying vec2 vTextureCoord;

void main() {
    gl_Position = aPosition;
    vTextureCoord = aTextureCoord;
}

precision mediump float;

uniform sampler2D tex;
uniform vec2 texel;

varying vec2 vTextureCoord;

void main() {
    vec4 d = vec4(texel.x, -texel.x, texel.y, -texel.y);
    vec4 s = texture2D(tex, vTextureCoord + d.xz)
        + texture2D(tex, vTextureCoord + d.xw)
        + texture2D(tex, vTextureCoord + d.yz)
        + texture2D(tex, vTextureCoord + d.yw);
    gl_FragColor = s * 0.25;
}
